import React, { useState, useEffect, useRef } from 'react';
import '../styles/Alltransactions.css';

const TransactionList = () => {
    const [searchTerm, setSearchTerm] = useState('');
    const [sortColumn, setSortColumn] = useState('');
    const [sortOrder, setSortOrder] = useState('asc');

    const searchInputRef = useRef(null);
    const tableRowsRef = useRef(null);
    const tableHeadingsRef = useRef(null);

    useEffect(() => {
        const searchTable = () => {
            tableRowsRef.current.forEach((row, i) => {
                const tableData = row.textContent.toLowerCase();
                const searchData = searchTerm.toLowerCase();

                row.classList.toggle('hide', tableData.indexOf(searchData) < 0);
                row.style.setProperty('--delay', i / 25 + 's');
            });

            document.querySelectorAll('tbody tr:not(.hide)').forEach((visibleRow, i) => {
                visibleRow.style.backgroundColor = i % 2 === 0 ? 'transparent' : '#0000000b';
            });
        };

        const sortTable = (column) => {
            const order = column === sortColumn && sortOrder === 'asc' ? 'desc' : 'asc';
            setSortColumn(column);
            setSortOrder(order);

            const sortedRows = [...tableRowsRef.current].sort((a, b) => {
                const firstRow = a.querySelectorAll('td')[column].textContent.toLowerCase();
                const secondRow = b.querySelectorAll('td')[column].textContent.toLowerCase();
                return order === 'asc' ? (firstRow < secondRow ? -1 : 1) : (firstRow > secondRow ? -1 : 1);
            });

            sortedRows.forEach(row => document.querySelector('tbody').appendChild(row));
        };

        const searchInput = searchInputRef.current;
        const tableRows = tableRowsRef.current;
        const tableHeadings = tableHeadingsRef.current;

        searchInput.addEventListener('input', searchTable);
        if (tableHeadings && tableHeadings.length > 0) {
        tableHeadings.forEach((head, i) => {
            head.onclick = () => sortTable(i);
        });}

        return () => {
            searchInput.removeEventListener('input', searchTable);
        };
    }, [searchTerm, sortColumn, sortOrder]);

    const toPDF = () => {
        // Code pour convertir en PDF
        alert('Converting to PDF...');
    };

    // Autres fonctions de conversion...

    return (
        <div className='alltransaction'>
            <main className="table" id="customers_table">
                <section className="table__header">
                    <h1>Transactions</h1>
                    <div className="input-group">
                        <input ref={searchInputRef} type="search" placeholder="Search Data..." value={searchTerm} onChange={(e) => setSearchTerm(e.target.value)} />
                        <img src="../assets/img/search.png" alt="" />
                    </div>
                    <div className="export__file">
                        <label htmlFor="export-file" className="export__file-btn" title="Télécharger"></label>
                        <input type="checkbox" id="export-file" />
                        <div className="export__file-options">
                            <label>Fichier &nbsp; &#10140;</label>
                            <label onClick={toPDF}>PDF <img src="../images/pdf.png" alt="" /></label>
                            {/* Autres options... */}
                        </div>
                    </div>
                </section>
                <section className="table__body">
                    <table>
                        <thead>
                            <tr ref={tableHeadingsRef}>
                                <th> Id <span className="icon-arrow">^</span></th>
                                <th> Participant<span className="icon-arrow">^</span></th>
                                <th> Type  <span className="icon-arrow">^</span></th>
                                <th> Date <span className="icon-arrow">^</span></th>
                                <th> Statut <span className="icon-arrow">^</span></th>
                                <th> Montant <span className="icon-arrow">^</span></th>
                            </tr>
                        </thead>
                        <tbody ref={tableRowsRef}>
                            <tr>
                                <td>125236</td>
                                <td>Albert</td>
                                <td>Dépôt</td>
                                <td>02/10/2023</td>
                                <td>Réussie</td>
                                <td>5000.0 xaf</td>
                            </tr>
                            <tr>
                                <td>125236</td>
                                <td>Albert</td>
                                <td>Dépôt</td>
                                <td>02/10/2023</td>
                                <td>Réussie</td>
                                <td>250000.0 xaf</td>
                            </tr>
                            <tr>
                                <td>125236</td>
                                <td>Albert</td>
                                <td>Dépôt</td>
                                <td>02/10/2023</td>
                                <td>Réussie</td>
                                <td>700.0 xaf</td>
                            </tr>
                            {/* Autres lignes de données... */}
                        </tbody>
                    </table>
                </section>
            </main>
        </div>
    );
}

export default TransactionList;
